import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function LoginPage() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
        const response = await fetch("http://localhost:5103/api/Auth/login", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ username, password }),
        });

        if (!response.ok) {
          const errorText = await response.text();  // Düz metin okuma
          throw new Error(errorText || "Giriş başarısız.");
        }

        const data = await response.json();
        console.log("Login başarılı:", data);

        // Başarı durumu sonrası yönlendirme veya başka işlemler
      } catch (error) {
        console.error("Login hatası:", error.message);
      }
  };

  return (
    <div>
      <h2>Giriş Yap</h2>
      <input
        placeholder="Kullanıcı Adı"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        placeholder="Şifre"
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Giriş Yap</button>
    </div>
  );
}
